const { Elements, Vector, Operator, Gates, Dimension, Cx, Circuit } = QuantumTensors;
const { MatrixViewer, KetListViewer, KetViewer } = BraKetVue;
const operator = Elements.beamSplitter(45);
const spinZ = Operator.fromSparseCoordNames([
  ['u', 'u', Cx(1)],
  ['d', 'd', Cx(-1)],
], [Dimension.spin()]);


new Vue({
  el: "#op-h",
  template: "<matrix-viewer :operator-raw='operator' :dark-mode='false' />",
  components: {
    MatrixViewer
  },
  data() {
    return {
        operator: Gates.H()
        }
  }
})
<!-- .addQubit(Vector.fromArray([Cx(0.5),Cx(0.1)],[Dimension.qubit()]),1) -->
const uvec=[];
Circuit.qubits(2)
  .saveTo(uvec)
  .addQubit(Vector.fromArray([Cx(0.9),Cx(0.1)],[Dimension.qubit()]),3)
  .saveTo(uvec);
  
  
function showState(elName, vector) {
  new Vue({
    el: elName,
    template: "<ket-viewer :vector='vector' :dark-mode='false'/>",
    components: {
      KetViewer,
    },
    data() {
      return { vector };
    },
  })
}


showState('#ket0', uvec[1].vector)


const temp=Vector.fromArray([Cx(0.9),Cx(0.1)],[Dimension.qubit()]);

showState('#ones',temp)






